                    pugi::xml_node grandchild = child.first_child();
                    while (grandchild) {
                        if (grandchild.type() == pugi::node_element) {
                            pugi::xml_node great_grandchild = grandchild.first_child();
                            while (great_grandchild) {
                                if (great_grandchild.type() == pugi::node_element) {
