def fibonacci(n):
    # Create a dictionary to store the Fibonacci numbers
    fib_dict = {0: 0, 1: 1}

    # Iterate from 2 to n
    for i in range(2, n + 1):
        # Calculate the ith Fibonacci number using the previous two numbers
