      // Set up screenshot and video recording
      on('task', {
        takeScreenshot: () => {
          return Cypress.Screenshot.take();
        },
        saveScreenshot: (screenshot) => {
          const screenshotPath = `${config.screenshotsFolder}/${screenshot.name}.png`;
          return cy.writeFile(screenshotPath, screenshot.data);
        },
      });

      // Set up Docker environment variables
      on('task', {
        getDockerEnv: () => {
          return Cypress.env('DOCKER_ENV');
        },
      });
    },
